const menuToggles=document.querySelectorAll(".js-menu-toggle"),menu=document.getElementById("site-menu"),skipLink=document.getElementById("skip-link");function toggleMenu(){menu.classList.toggle("is-active"),menu.toggleAttribute("hidden"),menu.getAttribute("hidden")==null?menu.focus():skipLink.focus()}menuToggles.forEach(e=>e.addEventListener("click",toggleMenu)),window.addEventListener("keydown",e=>{e.key==="Escape"&&menu.classList.contains("is-active")&&toggleMenu(),e.key==="/"&&e.metaKey&&toggleMenu()});const darkModeToggle=document.getElementById("mode-toggle");darkModeToggle.addEventListener("click",()=>{document.body.classList.toggle("is-dark-mode"),document.body.classList.toggle("is-light-mode")})